mapper.select:
SELECT qna_code, qna_title, qna_cont, member_id, DATE_FORMAT(qna_date, '%Y-%m-%d %H:%i:%s') as qna_date FROM item_qna
AND (qna_title LIKE CONCAT('%', #{search}, '%') OR member_id LIKE CONCAT('%', #{search}, '%'))
ORDER BY qna_code DESC LIMIT #{elementSize} OFFSET #{offset}

mapper.update:
UPDATE item_qna
SET
    qna_title = #{qnaTitle},
    qna_cont = #{qnaCont}
WHERE qna_code = #{qnaCode}

mapper.insert:
INSERT INTO item_qna_re (
    qna_code,
    re_cont
) VALUES (
             #{qnaCode},
             #{reCont}
         )

    SELECT LAST_INSERT_ID()
