mapper.select:
SELECT rev_code, rev_title, rev_cont, member_id, rev_date, item_id FROM ( SELECT ceil(ROWNUM / #{elementSize}) page, rev_code, rev_title, rev_cont, member_id, rev_date, item_id FROM ( SELECT rev_code, rev_title, rev_cont, member_id, to_char(rev_date, 'yyyy-MM-DD HH24:MI:SS') rev_date, item_id FROM item_rev
rev_title LIKE '%' || #{search} || '%' OR rev_code LIKE '%' || #{search} || '%' OR member_id = #{search}
ORDER BY rev_code DESC ) ) WHERE page = #{requestPage}

mapper.update:
UPDATE item_rev
SET rev_title = #{revTitle},
    rev_cont = #{revCont}
WHERE rev_code = #{revCode}

mapper.select:
SELECT
	oi.order_amount
FROM
	order_gr og
		JOIN member     m ON og.member_id = m.member_id
		JOIN order_item oi ON og.order_id = oi.order_id
WHERE
	og.member_id = #{memberId, jdbcType=VARCHAR}
  AND og.order_id = #{orderId, jdbcType=INTEGER}
  AND oi.order_item_id = #{itemId, jdbcType=INTEGER}
