mapper.insert:
INSERT INTO notice (
	noti_code,
	noti_title,
	noti_cont
) VALUES (
			 notice_seq.NEXTVAL,
			 #{notiTitle},
			 #{notiCont}
		 )

mapper.select:
SELECT noti_code, noti_title, noti_cont, noti_auth, noti_date FROM ( SELECT ceil(ROWNUM / #{elementSize}) page, noti_code, noti_title, noti_cont, noti_auth, noti_date FROM ( SELECT noti_code, noti_title, noti_cont, noti_auth, to_char(noti_date, 'yyyy-MM-DD HH24:MI:SS') noti_date FROM notice
noti_title LIKE '%' || #{search} || '%' OR noti_date LIKE '%' || #{search} || '%'
ORDER BY noti_code DESC ) ) WHERE page = #{requestPage}
